    document.cookie =
      "AuthToken=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
    window.location.reload();
  };

  return (
    <div className="chat-container-header">
      <div className="profile">
        <img src={user.url} alt={"photo of " + user.first_name} />
        <h3>{user.first_name}</h3>
      </div>

